type VideoSection struct {
	Data struct {
		List []struct {
			List []struct {
				Id    int    `json:"id"`
				Title string `json:"title"`
			} `json:"list"`
		} `json:"list"`
	} `json:"data"`
}

type SectionDetail struct {
	Data struct {
		Video struct {
			HighWatchUrl string `json:"highWatchUrl"`
			WathchUrl    string `json:"watchUrl"`
		}
	}
}

func httpPost(url string, d map[string]interface{}) string {
	jsonStr, _ := json.Marshal(d)

	req, err := http.NewRequest("POST", url, bytes.NewBuffer(jsonStr))
	req.Header.Set("Content-Type", "application/json")

	client := &http.Client{}
	resp, err := client.Do(req)
	if err != nil {
		panic(err)
	}
	defer resp.Body.Close()

	body, _ := ioutil.ReadAll(resp.Body)

	rsp := SectionDetail{}
	err = json.Unmarshal(body, &rsp)
	if err != nil {
		panic(err)
	}

	if rsp.Data.Video.HighWatchUrl != "" {
		return rsp.Data.Video.HighWatchUrl
	} else if rsp.Data.Video.WathchUrl != "" {
		return rsp.Data.Video.WathchUrl
	}
	return ""
}

func DownFile(url, path string) error {
	fout, err := os.Create(path)
	if err != nil {
		fmt.Println(err)
		return err
	}
	defer fout.Close()

	res, err := http.Get(url)
	if err != nil {
		fmt.Println("downfile error")
		return err
	}

	fileSize, err := strconv.ParseInt(res.Header.Get("Content-Length"), 10, 32)
	if err != nil {
		fmt.Println(err)
		return err
	}

	buf := make([]byte, 4096)
	countSize := 0
	totalSize := fileSize / 1024.0 / 1024.0
	for {
		size, _ := res.Body.Read(buf)
		if size == 0 {
			break
		} else {
			countSize += size
			//fmt.Printf("\r                                                                                            ")
			fmt.Printf("\r%v %vMb / %04.2f",
				path, totalSize, float64(countSize)/float64(fileSize))
			fout.Write(buf[:size])
		}
	}
	fmt.Printf("\r%v %vMb / Done\n", path, totalSize)
	return nil
}

